// Filter & Validate Debtors
// Based on WORKFLOW_ADAPTATION_PLAN.md Section 3.3 with all fixes applied

const items = $input.all();
const now = new Date();

// Business hours configuration
const BUSINESS_HOURS = {
  AEST: { weekday: { start: 7.5, end: 21 }, weekend: { start: 9, end: 21 } },
  ACST: { weekday: { start: 7.5, end: 21 }, weekend: { start: 9, end: 21 } },
  AWST: { weekday: { start: 7.5, end: 21 }, weekend: { start: 9, end: 21 } }
};

// Timezone offsets from UTC
const TIMEZONE_OFFSETS = {
  AEST: 10,
  ACST: 9.5,
  AWST: 8
};

function isBusinessHours(timezone) {
  const offset = TIMEZONE_OFFSETS[timezone] || 10; // Default AEST
  const localTime = new Date(now.getTime() + offset * 3600000);
  const hour = localTime.getHours() + localTime.getMinutes() / 60;
  const day = localTime.getDay();
  const isWeekend = day === 0 || day === 6;

  const hours = BUSINESS_HOURS[timezone] || BUSINESS_HOURS.AEST;
  const schedule = isWeekend ? hours.weekend : hours.weekday;

  return hour >= schedule.start && hour < schedule.end;
}

const filtered = items.filter(item => {
  const data = item.json;

  // Must be assigned to AI
  if (data.assigned_to_ai !== 'TRUE' && data.assigned_to_ai !== true) return false;

  // Must be unpaid
  if (data.payment_status !== 'unpaid') return false;

  // Must not be currently calling (duplicate prevention)
  if (data.call_status === 'calling') return false;

  // Must not be on do-not-call list
  if (data.do_not_call === 'TRUE' || data.do_not_call === true) return false;

  // Must have valid attempt number (1-3)
  const attemptNumber = parseInt(data.attempt_number) || 1;
  if (attemptNumber > 3) return false;

  // Must be scheduled for now or past
  if (data.next_call_date) {
    const nextCallDate = new Date(data.next_call_date);
    if (nextCallDate > now) return false;
  }

  // Must be within business hours for debtor's timezone
  const timezone = data.timezone || 'AEST';
  // console.log("business hour test" + isBusinessHours(timezone));
 if(!isBusinessHours(timezone)) 
   return false;

  // total attempts must me less then 9 and this  total attempt number is total completed attempts.
  if(parseInt(data.total_attempts) > 8)
    return false

  return true;
});

return filtered;

// Normalize Phone & Prepare Vapi Variables
  // FIXED: Proper international phone normalization

  const data = $input.first().json;

  // Phone normalization function - handles Australian, North American, and international
  function normalizePhone(phone) {
    if (!phone) return null;

    // Remove spaces, dashes, parentheses, dots
    let cleaned = phone.toString().replace(/[\s\-\(\)\.\+]/g, '');

    // Remove leading zeros/ones that might be trunk prefixes
    // But preserve if it's part of the actual number

    // Case 1: Already has country code (11+ digits, no leading 0)
    if (cleaned.length >= 11 && !cleaned.startsWith('0')) {
      // Starts with 61 = Australia
      if (cleaned.startsWith('61')) {
        return '+' + cleaned;
      }
      // Starts with 1 = North America
      if (cleaned.startsWith('1') && cleaned.length === 11) {
        return '+' + cleaned;
      }
      // Starts with 44 = UK
      if (cleaned.startsWith('44')) {
        return '+' + cleaned;
      }
      // Other international
      if (cleaned.length >= 11 && cleaned.length <= 15) {
        return '+' + cleaned;
      }
    }

    // Case 2: Australian local format (starts with 0, exactly 10 digits)
    if (cleaned.startsWith('0') && cleaned.length === 10) {
      return '+61' + cleaned.substring(1);
    }

    // Case 3: North American (exactly 10 digits, does NOT start with 0)
    if (cleaned.length === 10 && !cleaned.startsWith('0')) {
      return '+1' + cleaned;
    }

    // Case 4: Could be international without country code (7-9 digits)
    // This is ambiguous - throw error for manual review
    if (cleaned.length >= 7 && cleaned.length <= 9) {
      throw new Error(`Ambiguous phone number: ${phone}. Please include country code.`);
    }

    // Invalid
    throw new Error(`Invalid phone number: ${phone}. Got ${cleaned.length} digits: ${cleaned}`);
  }

  // Assistant selection (6 assistants based on debtor type and attempt number)
  const debtorType = (data.debtor_type || 'consumer').toLowerCase();
  const attemptNumber = parseInt(data.attempt_number) || 1;

  const assistantMap = {
    'consumer_1': $vars.VAPI_CONSUMER_1,
    'consumer_2': $vars.VAPI_CONSUMER_2,
    'consumer_3': $vars.VAPI_CONSUMER_3,
    'commercial_1': $vars.VAPI_COMMERCIAL_1,
    'commercial_2': $vars.VAPI_COMMERCIAL_2,
    'commercial_3': $vars.VAPI_COMMERCIAL_3
  };

  const assistantKey = `${debtorType}_${attemptNumber}`;
  const assistantId = assistantMap[assistantKey];

  if (!assistantId) {
    throw new Error(`No assistant found for ${assistantKey}`);
  }

  // Prepare Vapi call payload
  return {
    debtor_id: data.debtor_id,
    row_number: data.__rowNum__,
    phone_normalized: normalizePhone(data.phone),
    phone_original: data.phone,
    assistant_id: assistantId,
    vapi_payload: {
      assistantId: assistantId,
      customer: {
        number: normalizePhone(data.phone)
      },
      phoneNumberId: $vars.VAPI_PHONE_NUMBER_ID,
      assistantOverrides: {
        variableValues: {
          debtor_id: data.debtor_id.toString(),
          debtor_name: data.name,
          amount: data.amount.toString(),
          creditor: data.creditor,
          invoice_number: data.invoice_number,
          dob: data.dob || '',
          abn: data.abn || '',
          attempt_number: attemptNumber.toString(),
          collection_agency: $vars.ACCOUNT_NAME,
          bcs_phone_number: $vars.BCS_PHONE_NUMBER,
          stripe_payment_link: $vars.STRIPE_PAYMENT_LINK,
          bcs_mailing_address: $vars.BCS_MAILING_ADDRESS,
          agent_name: $vars.AGENT_NAME,
          Bank_name: $vars.BANK_NAME,
          BSB: $vars.BSB_NUMBER,
          Account_Number: $vars.ACCOUNT_NUMBER
        }
      }
    }
  };

