// Verify Webhook Signature
// Based on WORKFLOW_ADAPTATION_PLAN.md Section 4.2 with FIX #2 applied

const crypto = require('crypto');

const headers = Object($input.first().json.headers || {});
const signature = headers['x-signature'] ||  null;
// FIX #2: Header name is 'X-Vapi-Signature' (capital X) - verified from Vapi docs

const secret = $vars.VAPI_WEBHOOK_SECRET;
const timestamp = headers['x-timestamp'] || headers['X-Timestamp'];
const payload = JSON.stringify($input.first().json.body);
const signingString = `${timestamp}.${payload}`;

if (!signature) {
  throw new Error('Missing webhook signature');
}
console.log(secret);
const expectedSignature = crypto
  .createHmac('sha256', secret)
  .update(signingString)
  .digest('hex');

if (signature !== expectedSignature) {
  throw new Error('Invalid webhook signature');
}

return $input.first().json;

// Parse Call Data from Webhook
// Based on WORKFLOW_ADAPTATION_PLAN.md Section 4.3 with FIX #2 and #7 applied

// FIX #2: Updated webhook payload parsing based on Vapi docs verification
// const webhook = $input.item.json;
// const message_new = $input.first().json.body.message 


const message = $input.first().json.body.message;

// Verify this is an end-of-call-report event
if (message.type !== 'end-of-call-report') {
  throw new Error(`Unexpected webhook type: ${message.type}`);
}

const call = message.call;
const artifact = message.artifact || {};
console.log(artifact);
const endedReason = message.endedReason;

// Extract debtor_id from assistant overrides (stored in call object)
const debtorId = call.assistantOverrides?.variableValues?.debtor_id;

if (!debtorId) {
  throw new Error('Missing debtor_id in webhook');
}

// Check if call succeeded or failed
const callStatus = call.status; // "ended" or "failed"

// Parse function call (log_outcome)
// NOTE: Vapi may send this in artifact.messages array - needs testing
let outcome = 'UNKNOWN';
let paymentMethod = 'none';
let notes = '';
let promisedDate = null;

if (callStatus === 'failed') {
  // FIX #7: CALL_FAILED not in function enum - handle separately
  outcome = 'CALL_FAILED';
  notes = `Call failed: ${endedReason}`;
} else if (artifact.messages && artifact.messages.length > 0) {
  // Search for log_outcome function call in messages
  // Function calls may appear as toolCalls in message objects
  let functionCallFound = false;

  for (const msg of artifact.messages) {
    if (msg.toolCalls && Array.isArray(msg.toolCalls)) {
      for (const toolCall of msg.toolCalls) {
        if (toolCall.function?.name === 'log_outcome') {
          // Parse function arguments (may be string or object)
          const args = typeof toolCall.function.arguments === 'string'
            ? JSON.parse(toolCall.function.arguments)
            : toolCall.function.arguments;

          outcome = args.outcome || 'UNKNOWN';
          paymentMethod = args.payment_method || 'none';
          notes = args.notes || '';
          promisedDate = args.promised_date || null;
          functionCallFound = true;
          break;
        }
      }
      if (functionCallFound) break;
    }
  }

  if (!functionCallFound) {
    // No function call found = likely no answer, voicemail, or system error
    outcome = 'NO_ANSWER';
    notes = 'No outcome logged by assistant (check transcript)';
  }
} else {
  // No messages in artifact
  outcome = 'NO_ANSWER';
  notes = 'No conversation recorded';
}

return {
  debtor_id: debtorId,
  call_id: call.id,
  call_status: callStatus,
  recording_url: artifact.recording?.stereoUrl || call.recordingUrl || '',
  transcript: artifact.transcript || '',
  outcome: outcome,
  payment_method: paymentMethod,
  notes: notes,
  promised_date: promisedDate,
  // call_duration: call.endedAt && call.startedAt
  //   ? (new Date(call.endedAt) - new Date(call.startedAt)) / 1000
  //   : 0,
  call_duration: (() => {
  if (!message.startedAt || !message.endedAt) return 0;

  const start = new Date(message.startedAt).getTime();
  const end = new Date(message.endedAt).getTime();

  if (isNaN(start) || isNaN(end)) return 0;

  return (end - start) / 1000; // duration in seconds
})(),

};

// Calculate Next Action based on Outcome
// Based on WORKFLOW_ADAPTATION_PLAN.md Section 4.4

const data = $input.first().json;
const outcome = data.outcome;
const promisedDate = data.promised_date;
const callBackDate = data.callback_date;
const now = new Date();

let nextAction = null;
let nextCallDate = null;
let assignedToAi = true;
let paymentStatus = 'unpaid';
let doNotCall = false;

function addDays(date, days) {
  const result = new Date(date);
  result.setDate(result.getDate() + days);
  return result;
}

function addHours(date, hours) {
  const result = new Date(date);
  result.setHours(result.getHours() + hours);
  return result;
}

switch(outcome) {
  case 'PROMISE_TO_PAY':
    nextAction = 'SCHEDULE_CALL';
    if (promisedDate) {
      // Follow up day after promised date
      nextCallDate = addDays(new Date(promisedDate), 1);
    } else {
      // Default: follow up in 7 days
      nextCallDate = addDays(now, 7);
    }
    break;

  case 'READY_TO_PAY':
    nextAction = 'AWAIT_PAYMENT';
    // Payment instructions provided verbally during call
    // Schedule follow-up in 3 days to confirm payment received
    nextCallDate = addDays(now, 3);
    assignedToAi = false;
    break;

  case 'NO_ANSWER':
      nextAction = 'SCHEDULE_CALL';
      nextCallDate = addHours(now, 2); // Retry in 2 hours
      break;

  case 'VOICEMAIL':
    nextAction = 'SCHEDULE_CALL';
    nextCallDate = addHours(now, 2); // Retry in 4 hours
    break;

  case 'WRONG_NUMBER':
    nextAction = 'MANUAL_REVIEW';
    assignedToAi = false;
    break;

  case 'DISPUTE_RAISED':
      nextAction = 'MANUAL_REVIEW';
      assignedToAi = false;
      break;

  case 'HARDSHIP_CLAIMED':
    nextAction = 'MANUAL_REVIEW';
    assignedToAi = false;
    break;

  case 'REFUSED_TO_ENGAGE':
    nextAction = 'SCHEDULE_CALL';
    nextCallDate = addDays(now, 7); // Wait 1 week
    break;

  case 'REQUESTED_NO_CONTACT':
    nextAction = 'MANUAL_REVIEW';
    assignedToAi = false;
    doNotCall = true;
    break;

  case 'ALREADY_PAID':
    paymentStatus = 'paid';
    nextAction = 'CLOSE_ACCOUNT';
    assignedToAi = false;
    break;

  case 'CALL_FAILED':
    nextAction = 'SCHEDULE_CALL';
    nextCallDate = addHours(now, 2); // Retry in 2 hours
    break;

  case 'OBJECTED_TO_RECORDING':
    nextAction = 'MANUAL_REVIEW';
    assignedToAi = false;
    break;

  case 'PAYMENT_PLAN_REQUESTED':
    nextAction = 'MANUAL_REVIEW'; // human approval required
    assignedToAi = false;
    break;

  case 'PAYMENT_RECEIVED':
    nextAction = 'CLOSE_ACCOUNT'; // payment confirmed, close account
    paymentStatus = 'paid';
    assignedToAi = false;
    break;

  case 'DECEASED':
    nextAction = 'CLOSE_ACCOUNT'; // cease contact immediately
    assignedToAi = false;
    doNotCall = true;
    break;

  case 'CALLBACK_REQUESTED':
    nextAction = 'SCHEDULE_CALL'; // follow up at requested time
    if (callBackDate) {
      // Follow up day after promised date
      nextCallDate = new Date(callBackDate);
    } else {
      // Default: follow up in 2 hours
      nextCallDate = addHours(now, 2);
    }    
    break;

  case 'HUNG_UP':
    nextAction = 'SCHEDULE_CALL'; // retry next business day
    nextCallDate = addDays(now, 1);
    break;

  case 'ESCALATE_TO_HUMAN':
    nextAction = 'MANUAL_REVIEW'; // complex issue, needs human
    assignedToAi = false;
    break;

  case 'CEASE_CONTACT_REQUESTED':
    nextAction = 'MANUAL_REVIEW'; // cease calls, written contact only
    assignedToAi = false;
    doNotCall = true;
    break;

  case 'LEGAL_REPRESENTATION':
    nextAction = 'MANUAL_REVIEW'; // contact lawyer, stop debtor contact
    assignedToAi = false;
    doNotCall = true;
    break;

  case 'COMPLAINT_THREATENED':
    nextAction = 'MANUAL_REVIEW'; // escalate to supervisor
    assignedToAi = false;
    break;

  case 'AMOUNT_DISPUTED':
    nextAction = 'MANUAL_REVIEW'; // handle as dispute, pause collection
    assignedToAi = false;
    break;

  case 'ABUSIVE_DEBTOR':
    nextAction = 'MANUAL_REVIEW'; // flag for review, may cease contact
    assignedToAi = false;
    break;

  case 'BANKRUPTCY_DECLARED':
    nextAction = 'CLOSE_ACCOUNT'; // cease collection, contact trustee
    assignedToAi = false;
    doNotCall = true;
    break;

  default:
    nextAction = 'MANUAL_REVIEW';
    assignedToAi = false;
}

return {
  ...data,
  next_action: nextAction,
  next_call_date: nextCallDate ? nextCallDate.toISOString() : '',
  assigned_to_ai: assignedToAi,
  payment_status: paymentStatus,
  do_not_call: doNotCall
};

// Prepare Update Data
// Based on WORKFLOW_ADAPTATION_PLAN.md Section 4.6

const parsed = $('Calculate Next Action').first().json;
const currentRow = $('Lookup Debtor Row').first().json;

const currentAttempt = parseInt(currentRow.attempt_number) || 1;
const newAttempt = currentAttempt + 1;
const total_attempts = parseInt(currentRow.total_attempts) || 0;
const newTotalAttempts = total_attempts + 1;

const timestamp = new Date().toLocaleString('en-AU', { timeZone: 'Australia/Sydney' });
const newNotes = `${currentRow.notes || ''}\n[${timestamp}] Attempt ${currentAttempt}: ${parsed.outcome} - ${parsed.notes}`.trim();

return {
  row_number: currentRow.__rowNum__, // n8n provides this
  call_status: 'completed',
  call_id: parsed.call_id,
  recording_url: parsed.recording_url,
  last_call_date: new Date().toISOString(),
  last_outcome: parsed.outcome,
  next_action: parsed.next_action,
  next_call_date: parsed.next_call_date,
  attempt_number: newAttempt,
  notes: newNotes,
  assigned_to_ai: parsed.assigned_to_ai,
  payment_status: parsed.payment_status,
  do_not_call: parsed.do_not_call,
  payment_method: parsed.payment_method,
  debtor_id: parsed.debtor_id,
  total_attempts: newTotalAttempts
};

// Reset Daily Attempts
// Based on WORKFLOW_ADAPTATION_PLAN.md Section 3.7

const items = $input.all();
const now = new Date();
const today = now.toISOString().slice(0, 10); // YYYY-MM-DD format

const resetList = items
  .filter(item => {
    const data = item.json;

    // Must be assigned to AI
    if (data.assigned_to_ai !== 'TRUE' && data.assigned_to_ai !== true) return false;

    // Must be unpaid
    if (data.payment_status !== 'unpaid') return false;

    // Must not be currently calling (duplicate prevention)
    if (data.call_status === 'calling') return false;

    // Must not be on do-not-call list
    if (data.do_not_call === 'TRUE' || data.do_not_call === true) return false;

    const attemptNumber = parseInt(data.attempt_number) || 0;
    if (attemptNumber < 4) {
      // Optional â€” uncomment to only reset those who reached 3 attempts
      return false;
    }
    return true;
  })
  .map(item => {
    const data = item.json;

    return {
      json: {
        debtor_id: data.debtor_id,
        attempt_number: 1, // reset to 1
        last_reset_date: today,
        reset_reason: 'Daily reset of attempt count',
      },
    };
  });

return resetList;
