{
  "type": "function",
  "async": false,
  "function": {
    "name": "log_outcome",
    "description": "Log the call outcome when conversation ends. MUST be called before ending call.",
    "parameters": {
      "type": "object",
      "properties": {
        "outcome": {
          "type": "string",
          "enum": [
            "PROMISE_TO_PAY",
            "READY_TO_PAY",
            "NO_ANSWER",
            "VOICEMAIL",
            "WRONG_NUMBER",
            "REFUSED_TO_ENGAGE",
            "REQUESTED_NO_CONTACT",
            "ALREADY_PAID",
            "DISPUTE_RAISED",
            "HARDSHIP_CLAIMED",
            "OBJECTED_TO_RECORDING",
            "PAYMENT_PLAN_REQUESTED",
            "PAYMENT_RECEIVED",
            "DECEASED",
            "CALLBACK_REQUESTED",
            "HUNG_UP",
            "ESCALATE_TO_HUMAN",
            "CEASE_CONTACT_REQUESTED",
            "LEGAL_REPRESENTATION",
            "COMPLAINT_THREATENED",
            "AMOUNT_DISPUTED",
            "ABUSIVE_DEBTOR",
            "BANKRUPTCY_DECLARED",
            "IDENTITY_VERIFICATION_FAILED"
          ],
          "description": "The outcome of the call"
        },
        "payment_method": {
          "type": "string",
          "enum": ["credit_card", "bank_transfer", "cheque", "none"],
          "description": "Preferred payment method if debtor is ready to pay"
        },
        "notes": {
          "type": "string",
          "description": "Free-form summary of the conversation"
        },
        "promised_date": {
          "type": "string",
          "format": "date",
          "description": "ISO date string if debtor promised to pay by specific date"
        },
        "callback_date": {
          "type": "string",
          "format": "date-time",
          "description": "ISO datetime if debtor requested callback"
        }
      },
      "required": ["outcome", "notes"]
    }
  }
}

{
  "type": "function",
  "async": false,
  "function": {
    "name": "verify_identity",
    "description": "Verify debtor identity before discussing debt details",
    "parameters": {
      "type": "object",
      "properties": {
        "verification_method": {
          "type": "string",
          "enum": ["dob", "abn", "address"],
          "description": "Method used for verification"
        },
        "verified": {
          "type": "boolean",
          "description": "Whether identity was successfully verified"
        },
        "notes": {
          "type": "string",
          "description": "Additional notes about verification"
        }
      },
      "required": ["verification_method", "verified"]
    }
  }
}
